mapper.insert:
insert into storage(g_id, quantity, quality, il_id)
select goods_info.g_id, #{quantity}, #{quality}, #{il_id}
from goods_info
where goods_info.name = #{name}

mapper.select:
select storage.i_id, storage.g_id, storage.quantity, storage.quality, storage.il_id
from goods_info, storage
where storage.g_id = goods_info.g_id and goods_info.name = #{name}

mapper.update:
update storage
set quantity = #{quantity},
    quality = #{quality}
where i_id = #{i_id}

update storage, goods_info
set storage.quantity = #{quantity} + storage.quantity, storage.quality = #{quality}
where storage.g_id = goods_info.g_id and goods_info.name = #{name} and storage.il_id = #{il_id}

mapper.select:
select i_id, g_id, quantity, quality
from storage
where g_id = #{g_id}

select storage_list.storage_name, storage_list.il_id
from storage_list
where storage_list.il_id = #{il_id}
